INSERT INTO "public"."products" ("id", "name", "description", "price", "image_url", "category", "weight", "stock_quantity", "is_active", "created_at", "updated_at", "long_description") VALUES ('fceb916d-9bb6-4efb-a882-734877fc4322', 'Chole-Rajma Masala Gravy', '', '199.00', 'assets/product_images/', 'gravy', '200g', '100', 'true', '2025-12-20 10:43:52.498467+00', '2025-12-20 10:43:52.498467+00', 'A rich and flavourful gravy crafted with traditional Indian spices, perfect for preparing comforting chole or rajma dishes. Slow-cooked to bring out deep, homestyle flavours. Simply add cooked beans and enjoy a hearty, wholesome meal in minutes.'), ('31e6cf8c-efbd-4a35-a5cc-ed1cb1422cec', 'Dhaba Chicken', '', '199.00', 'assets/product_images/', 'gravy', '200g', '100', 'true', '2025-12-20 10:43:52.498467+00', '2025-12-20 10:43:52.498467+00', 'Inspired by the bold flavours of roadside dhabas, this gravy delivers a robust, spicy, and smoky taste. Made with hand-picked spices for an authentic North Indian experience. Perfect for creating restaurant-style chicken curry at home with ease.'), ('ed933ee2-9ee0-4818-9983-3ff665a52aca', 'Fish Fry', '', '199.00', 'assets/product_images/', 'marinade', '200g', '100', 'true', '2025-12-20 10:43:52.498467+00', '2025-12-20 10:43:52.498467+00', 'A perfectly balanced spice blend designed to coat fish with crispiness and flavour. Infused with traditional coastal spices that enhance the natural taste of seafood. Ideal for quick, golden, and delicious fish fry every time.'), ('c2238eab-edb7-49ad-bd82-5c068e0fc842', 'Hyderabadi Biryani Gravy', '', '199.00', 'assets/product_images/', 'gravy', '200g', '100', 'true', '2025-12-20 10:43:52.498467+00', '2025-12-20 10:43:52.498467+00', 'An aromatic and royal gravy inspired by the legendary Hyderabadi biryani. Rich in whole spices and herbs, it delivers authentic depth and fragrance. Just add rice and meat or vegetables to recreate a classic biryani effortlessly.'), ('13567dcc-65e7-4852-8a7b-0bfe4f092981'